mapper.select:
select
*
from order_main
where status=#{status}
order by create_time desc
limit #{offset},#{limit};

mapper.update:
update order_main set status = 1
where
id = #{id};

mapper.select:
SELECT *
FROM order_main WHERE status = 0
LIMIT #{offset},#{limit}

